export const decks = {
  Capital: {
    title: 'Capital',
    questions: [
      {
        question: 'Zagreb is the capital of which country?',
        answer: 'Croatia'
      },
      {
        question: 'Tbilisi is the capital of which country?',
        answer: ' Georgia'
      },
      {
        question: 'Santiago is the capital of which country?',
        answer: 'Chile'
      }
    ]
  },
  Redux: {
    title: 'Redux',
    questions: [
      {
        question: 'What is Redux?',
        answer: 'A predictable state container for JavaScript Apps'
      },
      {
        question: 'What is a reducer?',
        answer:
          'A reducer is a pure function that takes the current state and action and returns the next state.'
      },
      {
        question: 'What is an action creator?',
        answer: 'It is a function that takes an input and returns an object with a type and data property.'
      }
    ]
  },
  Movies: {
    title: 'Movies',
    questions: [
      {
        question: ' Which war movie won the Academy Award for Best Picture in 2009?',
        answer: 'The Hurt Locker'
      },
      {
        question: 'What is the name of the hobbit played by Elijah Wood in the Lord of the Rings movies?',
        answer: 'Frodo Baggins'
      }
    ]
  },
};
